import React from "react";

import {Counter} from "@ya.praktikum/react-developer-burger-ui-components";
import {CurrencyIcon} from "@ya.praktikum/react-developer-burger-ui-components";
import {Tab} from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "../burger-ingredients/burger-ingredients.module.css";

function BurgerIngredients(props) {
    const [current, setCurrent] = React.useState('one')
    return (
        <div className={styles.content}>
            <h2 className={`${styles.title} text text_type_main-large pl-5 mt-10 mb-5`}>Соберите бургер</h2>
            <div className={styles.tabs}>
                <Tab value="one" active={current === 'one'} onClick={setCurrent}>
                    Булки
                </Tab>
                <Tab value="two" active={current === 'two'} onClick={setCurrent}>
                    Соусы
                </Tab>
                <Tab value="three" active={current === 'three'} onClick={setCurrent}>
                    Начинки
                </Tab>
            </div>
            <div className="custom-scroll">
                <h3 className={`${styles.title} text text_type_main-medium pl-5 mt-10 mb-5 pb-6`}>Булки</h3>
                <div className={styles.grid}>
                    <div className={styles.ingredient}></div>
                </div>
                <h3 className={`${styles.title} text text_type_main-medium pl-5 mt-10 mb-5 pb-6`}>Соусы</h3>
                <h3 className={`${styles.title} text text_type_main-medium pl-5 mt-10 mb-5 pb-6`}>Начинки</h3>
            </div>
        </div>
    );
}

export default BurgerIngredients;
